        function BSTWC_wordpress_page() {

            add_menu_page('BSTWC', 'BSTWC', 'publish_pages', 'BSTWC_main_BSTWC_page_slug', array($this,'build_main_BSTWC_page'),'data:image/svg+xml;base64,',1);
